// client/src/pages/admin/AdminLogin.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../utils/api';
import { setToken } from '../../utils/auth';

const AdminLogin = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const res = await api.post('/auth/login', form);
      // backend kamu mengembalikan { token }
      setToken(res.data.token);
      navigate('/admin', { replace: true });
    } catch (err) {
      setError(err?.response?.data?.message || 'Gagal login.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background">
      <form
        onSubmit={handleSubmit}
        className="glass-card w-full max-w-sm p-6 space-y-4"
        autoComplete="off"
      >
        <h1 className="text-lg font-semibold text-text">Login Admin</h1>

        {error && (
          <p className="text-xs text-state-error bg-state-error/10 rounded-xl px-3 py-2">
            {error}
          </p>
        )}

        <div>
          <label className="block text-xs text-text-muted mb-1">Email</label>
          <input
            type="email"
            name="email"
            autoComplete="new-email"
            className="w-full rounded-xl border border-white/10 bg-background px-3 py-2 text-sm text-text"
            value={form.email}
            onChange={handleChange}
            placeholder="admin@xirpl.com"
          />
        </div>

        <div>
          <label className="block text-xs text-text-muted mb-1">Password</label>
          <input
            type="password"
            name="password"
            autoComplete="new-password"
            className="w-full rounded-xl border border-white/10 bg-background px-3 py-2 text-sm text-text"
            value={form.password}
            onChange={handleChange}
            placeholder="••••••••"
          />
        </div>

        <button
          type="submit"
          disabled={loading}
          className="w-full rounded-xl bg-primary text-white text-sm font-medium py-2.5 hover:bg-primary/90 disabled:opacity-60"
        >
          {loading ? 'Masuk...' : 'Masuk'}
        </button>
      </form>
    </div>
  );
};

export default AdminLogin;
